import React, { useState } from "react";
import "./App.css";
import Wheather from "./Wheather";

function App() {
  const APP_KEY = "d6a3f7e326d0454ea4982708233105";
  let cityinput = "";
  const [wheatherdata, setwheatherdata] = useState([]);
  function citytext() {
    document.querySelector("input").addEventListener("input", (e) => {
      e.preventDefault();
      cityinput = e.target.value;
      console.log(cityinput);
    });
  }
  async function getdata(value) {
    const data =
      await fetch(`http://api.weatherapi.com/v1/forecast.json?key=&q=London&days=3&aqi=no&alerts=no
`);
  }
  return (
    <div>
      <div className="search">
        <input type="text" placeholder="Search a city..." onChange={citytext} />
        <button onClick={() => getdata(cityinput)}>Search</button>
      </div>
      {/* <Wheather /> */}
    </div>
  );
}

export default App;
